import React, { useState } from 'react';
import Header from './components/Header';
import SimulationWindow from './components/SimulationWindow';
import KPISection from './components/KPISection';
import AIDecisionLog from './components/AIDecisionLog';
import MapViewToggle from './components/MapViewToggle';
import ControlPanel from './components/ControlPanel';

function App() {
  const [currentScenario, setCurrentScenario] = useState('normal');
  const [manualOverride, setManualOverride] = useState(false);
  const [mapView, setMapView] = useState('sumo');

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(to bottom right, #111111, #1a1a1a, #111111)',
      color: 'white'
    }}>
      <Header />

      <main style={{ position: 'relative', padding: '32px', display: 'flex', flexDirection: 'column', gap: '32px' }}>
        {/* Background grid pattern */}
        <div style={{
          position: 'fixed',
          inset: '0',
          opacity: '0.1',
          pointerEvents: 'none',
          backgroundImage: `
            linear-gradient(rgba(0, 255, 255, 0.1) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0, 255, 255, 0.1) 1px, transparent 1px)
          `,
          backgroundSize: '30px 30px'
        }}></div>

        {/* Top Section - Simulation and Control */}
        <div style={{
          position: 'relative',
          zIndex: '10',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '32px'
        }}>
          <div style={{ gridColumn: 'span 2' }}>
            <SimulationWindow />
          </div>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
            <ControlPanel 
              currentScenario={currentScenario}
              setCurrentScenario={setCurrentScenario}
              manualOverride={manualOverride}
              setManualOverride={setManualOverride}
            />
          </div>
        </div>

        {/* Middle Section - KPIs and Decision Log */}
        <div style={{
          position: 'relative',
          zIndex: '10',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
          gap: '32px'
        }}>
          <div style={{ gridColumn: 'span 2' }}>
            <KPISection />
          </div>
          <div>
            <AIDecisionLog />
          </div>
        </div>

        {/* Bottom Section - Map Views */}
        <div style={{ position: 'relative', zIndex: '10' }}>
          <MapViewToggle 
            activeView={mapView}
            setActiveView={setMapView}
          />
        </div>
      </main>

      {/* Ambient lighting effects */}
      <div style={{
        position: 'fixed',
        top: '25%',
        left: '25%',
        width: '384px',
        height: '384px',
        background: 'rgba(0, 255, 128, 0.1)',
        borderRadius: '50%',
        filter: 'blur(48px)',
        pointerEvents: 'none'
      }}></div>
      <div style={{
        position: 'fixed',
        bottom: '25%',
        right: '25%',
        width: '384px',
        height: '384px',
        background: 'rgba(0, 255, 255, 0.1)',
        borderRadius: '50%',
        filter: 'blur(48px)',
        pointerEvents: 'none'
      }}></div>
    </div>
  );
}

export default App;
