import React, { useState } from 'react';
import Header from './components/Header';
import SimulationWindow from './components/SimulationWindow';
import KPISection from './components/KPISection';
import AIDecisionLog from './components/AIDecisionLog';
import MapViewToggle from './components/MapViewToggle';
import ControlPanel from './components/ControlPanel';

function App() {
  const [currentScenario, setCurrentScenario] = useState('normal');
  const [manualOverride, setManualOverride] = useState(false);
  const [mapView, setMapView] = useState('sumo');

  return (
    <div className="min-h-screen bg-gradient-to-br from-void-900 via-void-800 to-void-900">
      <Header />

      <main className="relative p-8 space-y-8">
        {/* Background grid pattern */}
        <div className="fixed inset-0 data-grid opacity-10 pointer-events-none"></div>

        {/* Top Section - Simulation and Control */}
        <div className="relative z-10 grid grid-cols-1 lg:grid-cols-4 gap-8 animate-fade-in">
          <div className="lg:col-span-3">
            <SimulationWindow />
          </div>
          <div className="space-y-6">
            <ControlPanel 
              currentScenario={currentScenario}
              setCurrentScenario={setCurrentScenario}
              manualOverride={manualOverride}
              setManualOverride={setManualOverride}
            />
          </div>
        </div>

        {/* Middle Section - KPIs and Decision Log */}
        <div className="relative z-10 grid grid-cols-1 xl:grid-cols-3 gap-8 animate-slide-up">
          <div className="xl:col-span-2">
            <KPISection />
          </div>
          <div>
            <AIDecisionLog />
          </div>
        </div>

        {/* Bottom Section - Map Views */}
        <div className="relative z-10 animate-slide-up">
          <MapViewToggle 
            activeView={mapView}
            setActiveView={setMapView}
          />
        </div>
      </main>

      {/* Ambient lighting effects */}
      <div className="fixed top-1/4 left-1/4 w-96 h-96 bg-neon-500/10 rounded-full blur-3xl pointer-events-none"></div>
      <div className="fixed bottom-1/4 right-1/4 w-96 h-96 bg-cyan-500/10 rounded-full blur-3xl pointer-events-none"></div>
    </div>
  );
}

export default App;
